import ReactTable from "react-table";
import "react-table/react-table.css";
import TableData from "../data/data.json";
import React from "react";

const columnKey = Object.keys(TableData[0]);
var columns = [];

const sort_by = (field, reverse, primer) => {
  const key = primer
    ? function(x) {
        return primer(x[field]);
      }
    : function(x) {
        return x[field];
      };

  reverse = !reverse ? 1 : -1;

  return function(a, b) {
    return (a = key(a)), (b = key(b)), reverse * ((a > b) - (b > a));
  };
};

export default props => {
  console.log(props);

  if (columns.includes(props)) {
    if (props === "City" || props === "Country") {
      TableData.sort(sort_by(props, false, a => a.toUpperCase()));
    } else TableData.sort(sort_by(props, false, parseInt));
  }

  columnKey.forEach(key =>
    columns.push({
      Header: key,
      accessor: key
    })
  );

  return (
    <div>
      <ReactTable data={TableData} columns={columns} />
    </div>
  );
};
